import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API_URL from '../apiConfig';

const OpenAccount = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    bankId: '',
  });
  const [banks, setBanks] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false); // Added loading state
  const [phoneError, setPhoneError] = useState(''); // Added for phone validation feedback

  // Fetch available banks on component mount
  useEffect(() => {
    setLoading(true);
    axios.get(`${API_URL}/api/auth/banks`)
      .then(res => {
        setBanks(res.data || []);
      })
      .catch(err => {
        console.error('Fetch Banks Error:', err);
        setMessage(err.response?.data?.msg || 'Error fetching banks');
      })
      .finally(() => setLoading(false));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Validate phone number on change
    if (name === 'phone') {
      const phoneRegex = /^\d{10}$/;
      if (value && !phoneRegex.test(value)) {
        setPhoneError('Please enter a valid 10-digit phone number');
      } else {
        setPhoneError('');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prevent submission if phone number is invalid
    if (phoneError) {
      setMessage('Please fix the errors in the form');
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(`${API_URL}/api/auth/signup`, formData);
      setMessage(res.data.msg);
      setFormData({ name: '', email: '', phone: '', password: '', bankId: '' }); // Reset form data
      setTimeout(() => {
        if (onClose) onClose();
      }, 2000);
    } catch (err) {
      setMessage(err.response?.data?.msg || 'Error opening account');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="open-account-form">
      <h2 className="form-title">Open Account</h2>
      {message && <p className="form-message">{message}</p>}
      {loading && <p className="form-message">Loading...</p>}
      <div className="form-group">
        <label className="form-label">Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className="form-input"
          placeholder="Enter your name"
          required
        />
      </div>
      <div className="form-group">
        <label className="form-label">Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className="form-input"
          placeholder="Enter your email"
          required
        />
      </div>
      <div className="form-group">
        <label className="form-label">Phone</label>
        <input
          type="text"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          className="form-input"
          placeholder="Enter your phone number (10 digits)"
          required
        />
        {phoneError && <p className="form-error">{phoneError}</p>}
      </div>
      <div className="form-group">
        <label className="form-label">Password</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          className="form-input"
          placeholder="Enter your password"
          required
        />
      </div>
      <div className="form-group">
        <label className="form-label">Bank</label>
        <select
          name="bankId"
          value={formData.bankId}
          onChange={handleChange}
          className="form-input"
          required
          disabled={loading || banks.length === 0}
        >
          <option value="">Select Bank</option>
          {banks.map(bank => (
            <option key={bank._id} value={bank._id}>{bank.name}</option>
          ))}
        </select>
      </div>
      <button
        onClick={handleSubmit}
        className="form-button"
        disabled={loading || phoneError || banks.length === 0}
      >
        {loading ? 'Submitting...' : 'Submit'}
      </button>
    </div>
  );
};

export default OpenAccount;
